import React, {useState} from "react";
import "./downtime.css";
import Carousing from "./carousing";
import Crafting from "./crafting";
import Crime from "./crime";
import Gambling from "./gambling";
import PitFighting from "./pit-fighting";
import Relaxation from "./relaxation";
import ReligiousService from "./religious-service";
import Research from "./research";
import MonsterHunting from "./monster-hunting";
import Scribing from "./scribing";


const sectionData = [
    { title: "Carousing", id: "carousing" },
    { title: "Crafting", id: "crafting" },
    { title: "Crime", id: "crime" },
    { title: "Gambling", id: "gambling" },
    { title: "Pit Fighting", id: "pit-fighting" },
    { title: "Relaxation", id: "relaxation"},
    { title: "Religious Service", id: "religious-service" },
    { title: "Research", id: "research" },
    { title: "Monster Research / Monster Hunting for Parts", id: "monster-hunting" },  
    { title: "Scribing", id: "scribing" },
    { title: "Selling Magic Items", id: "selling-magic-items" },
    { title: "Spellwrought Tatoos", id: "spellwrought-tattoos" },
    { title: "Training", id: "training" },
    { title: "Training Animals", id: "training-animals" },
    { title: "Work", id: "work" }
];
  

function Downtime(){
    const [currentSection, setCurrentSection] = useState(sectionData[0].id);
    
    const handleSectionClick = (id) => {
        const section = document.getElementById(id);
        if (section) {
        section.scrollIntoView({ behavior: "smooth" });
        setCurrentSection(id);
        }
            };
    
    return (
        <div className="downtime-container">
            <div className="table-of-contents">
            <h3>Table of Contents</h3>
            <ul>
            {sectionData.map((section) => (
                <li key={section.id}>
                <h4
                    onClick={() => handleSectionClick(section.id)}
                    className={section.id === currentSection ? "current-section" : ""}
                >
                    {section.title}
                </h4>
                </li>
            ))}
            </ul>
        </div>
        <div className="downtime-content">
            <Carousing></Carousing>
            <hr></hr>
            <Crafting></Crafting>
            <hr></hr>
            <Crime></Crime>
            <hr></hr>
            <Gambling></Gambling>
            <hr></hr>
            <PitFighting></PitFighting>
            <hr></hr>
            <Relaxation></Relaxation>
            <hr></hr>
            <ReligiousService></ReligiousService>
            <hr></hr>
            <Research></Research>
            <hr></hr>
            <MonsterHunting></MonsterHunting>
            <hr></hr>
            <Scribing></Scribing>
        </div>
</div>
    )
}

export default Downtime;
