import React, {useState} from "react";
import "./downtime.css";

const sectionData = [
    { title: "Carousing", id: "carousing" },
    { title: "Crafting", id: "crafting" },
    { title: "Crime", id: "crime" },
    { title: "Gambling", id: "gambling" },
    { title: "Pit Fighting", id: "pit-fighting" },
    { title: "Religious Service", id: "religious-service" },
    { title: "Research", id: "research" },
    { title: "Monster Research / Monster Hunting for Parts", id: "monster-hunting" },  
    { title: "Scribing", id: "scribing" },
    { title: "Selling Magic Items", id: "selling" },
    { title: "Spellwrought Tatoos", id: "spellwrought-tattoos" },
    { title: "Training", id: "training" },
    { title: "Training Animals", id: "training" },
    { title: "Work", id: "work" }
];
  

function Downtime(){
    const [currentSection, setCurrentSection] = useState(sectionData[0].id);
    
    const handleSectionClick = (id) => {
        const section = document.getElementById(id);
        if (section) {
        section.scrollIntoView({ behavior: "smooth" });
        setCurrentSection(id);
        }
            };
    
    return (
        <div className="downtime-container">
            <div className="table-of-contents">
            <h3>Table of Contents</h3>
            <ul>
            {sectionData.map((section) => (
                <li key={section.id}>
                <h4
                    onClick={() => handleSectionClick(section.id)}
                    className={section.id === currentSection ? "current-section" : ""}
                >
                    {section.title}
                </h4>
                </li>
            ))}
            </ul>
        </div>
        <div className="downtime-content">

        </div>
</div>
    )
}

export default Downtime;
